import { BaseTableCol, ThMap } from './types';
/**
 * 更新拖动后的列宽记录
 * @param dragCol 被拖动的列
 * @param dragWidth 拖动大小
 * @param effectCol 受影响的列
 * @param options 配置参数
 * @param callback 回调函数
 */
export default function setThWidthListByColumnDrag<T extends BaseTableCol<T>>(dragCol: T, dragWidth: number, effectCol: T, options: {
    getThWidthList: () => ThMap;
    DEFAULT_MIN_WIDTH: number;
}, callback: (widthMap: ThMap, colKeys: string[]) => void): void;
