import { BaseTableCol, ThMap } from './types';
/**
 * 重新按规则分配各列宽度
 * @param columns 当前表格所有列
 * @param thWidthList 列宽记录字典
 * @param tableLayout 表格布局
 * @param tableElmWidth 表格宽度
 * @param notCalculateWidthCols 不需要参与计算的列
 * @param callback 回调函数
 */
export default function recalculateColumnWidth<T extends BaseTableCol<T>>(columns: T[], thWidthList: ThMap, tableLayout: string, tableElmWidth: number, notCalculateWidthCols: string[], callback: (widthMap: {
    [colKey: string]: number;
}) => void): void;
